import React from "react";
import waterRive , { useRive,useStateMachineInput } from './715-1406-water-bar-demo.riv'

const WaterBar = ({scroll}) => {


    const STATE_MACHINE_NAME = 'State Machines'
    const INPUT_NAME_1 = 'Level'

    const { rive, RiveComponent } = useRive({
        src: waterRive,
        stateMachines:STATE_MACHINE_NAME,
        artboard: 'New Artboard',
        autoplay: true,
        // layout: new Layout({ fit: Fit.Cover }), 
        // fit: rive.layout.alignment.
        // onStateChange: (e) => {
        //     console.log(e.data[0]);
        // }
    });

    // pressedInput in a trigger state machine input. To transition the state
    // we need to call the `fire()` method on the input.
    const ValueInput = useStateMachineInput(
        rive,
        STATE_MACHINE_NAME,
        INPUT_NAME_1
    );


    return (

        <RiveComponent
            onMouseEnter={() => ValueInput.fire() }
            render={()=> ValueInput.value = scroll}
        />
    );

}

export default waterRive
