import React from "react";
import Rive,{ useRive,useStateMachineInput } from 'rive-react';

import waterRive  from './715-1406-water-bar-demo.riv'

const WaterBar = ({scroll=0}) => {


    const STATE_MACHINE_NAME = 'State Machines'
    const INPUT_NAME_1 = 'Level'

    const { rive, RiveComponent } = useRive({
        src: waterRive,
        stateMachines:STATE_MACHINE_NAME,
        artboard: 'New Artboard',
        autoplay: true,

    });

    const ValueInput = useStateMachineInput(
        rive,
        STATE_MACHINE_NAME,
        INPUT_NAME_1
    );


    return (

        <RiveComponent
            // onMouseEnter={() => ValueInput.value = true }
            onClick={() => ValueInput.value = scroll?0:scroll }
            // onclick = {() => ValueInput.fire()}
        />
    );

}

export default WaterBar
