import React, { useState, useEffect, useRef } from "react";
import WaterBar from "./Rive-Components/WaterBar.component.js";
import TodoList from './SubComponents/TodoComponent'
import useMouse from "@react-hook/mouse-position";


const ListTask = (a) => {

  const [colorBack, setColorBack] = useState('rgb(3,15,22)');
  const [colorFont, setColorFont] = useState('white');

  const ref = useRef(null)
  const mouse =  useMouse(ref, {
    enterDelay: 100,
    leaveDelay: 100,
  })

  console.log(mouse.pageY);


  return (
    <div 
    ref={ref}
    style={{
        width: "100vw",
        height: "100vh",
        // PaddingTop : "50px",
        paddingLeft: '20px',
        background:colorBack,
        color: colorFont,
        display:'inline-flex',

      }}
    >
      <TodoList 
        style={{
          width: "40vw",
          paddingTop : "50px",
          // paddingLeft : "30px"
          }}
      />
      <WaterBar 
        style={{
            width:'50vw',
            position:'absolute',
            top:0,
            left:0
            // marginLeft: '10vw',
            // zIndex:-1,
            // overflow: 'hidden',
        }}
        scroll={mouse.pageY/80} 
        colorBack={colorBack} 
      />
    </div>
  );
};

export default ListTask;
