import React from "react";
import { useRive,useStateMachineInput } from 'rive-react';

import waterRive  from './715-1406-water-bar-demo.riv'
import TodoList from './SubComponents/TodoComponent'


const WaterBar = ({points}) => {


    const STATE_MACHINE_NAME = 'State Machine'
    const INPUT_NAME_1 = 'Level'

    const { rive, RiveComponent } = useRive({
        src: waterRive,
        stateMachines:STATE_MACHINE_NAME,
        artboard: 'New Artboard',
        autoplay: true,

    });

    const ValueInput = useStateMachineInput(
        rive,
        STATE_MACHINE_NAME,
        INPUT_NAME_1
    );



    return (

        <div>

        <RiveComponent
            style={{
                width:'500px',
                height:'500px',
                // paddingTop:'20%',
                paddingLeft:'30%'
                // // position:'absolute',
                // top:0,
                // left:0,

            }}
            onClick={
                () => ValueInput.value=(points===null?0:points)
            }
        />
        </div>
    );

}

export default WaterBar
