import React, { useState, useEffect, useRef } from "react";
import WaterBar from "./Rive-Components/WaterBar.component.js";
import TodoList from './SubComponents/TodoComponent'
import useMouse from "@react-hook/mouse-position";


const ListTask = (a) => {

  const [colorBack, setColorBack] = useState('rgb(3,15,22)');
  const [colorFont, setColorFont] = useState('white');

  const ref = useRef(null)
  const mouse =  useMouse(ref, {
    enterDelay: 100,
    leaveDelay: 100,
  })



  return (
    <div 
    ref={ref}
    style={{
        width: "90vw",
        height: "100vh",
        paddingLeft: '20px',
        marginTop:'20px',
        color: colorFont,
        overflow:'hidden',

      }}
    >
      <TodoList 
        style={{
          width: "100%",
          zIndex:2
          }}
      />
      <div
        style={{
          width:'100%',
          height:'100%',
          position:'absolute',
          top:'40vw',
          left:'0',
          zIndex:-1,
          backgroundColor:'black',
          overflow:'hidden',
      }}
      >

      <WaterBar 
          style={{
                width:'500px',
                height:'500px',
                paddingTop:'20%',
                paddingLeft:'30%'
                // // position:'absolute',
                // top:0,
                // left:0,

            }}

        scroll={mouse.pageY/80} 
        colorBack={colorBack} 
      />

      </div>

    </div>
  );
};

export default ListTask;
