import React, { useState, useRef } from "react";
import WaterBar from "./Rive-Components/WaterBar.component.js";
import TodoList from './SubComponents/TodoComponent'


const ListTask = (a) => {

  const [colorBack, setColorBack] = useState('rgb(3,15,22)');
  const [colorFont, setColorFont] = useState('white');
  const [points,setPoints] = useState(0);
  const [trigger,setTrigger] = useState(false);


  return (
    <div 
    style={{
        width: "100vw",
        height: "100vh",
        // paddingLeft: '20px',
        // marginTop:'20px',
        padding:'20px 0px 0px 20px',
        color: colorFont,
        overflow:'hidden',
        display:'flex'

      }}
    >
      <TodoList 
        style={{
          width: "100%",
          zIndex:2
          }}
        points={points}
        setPoints={setPoints}
      />
      <div
        style={{
          width:'100%',
          height:'100%',
          position:'absolute',
          top:'0',
          left:'0',
          zIndex:-1,
          backgroundColor:'rgb(11,14,21)',
          overflow:'hidden',
        }}
      >
      <WaterBar 
        points={points*10} 
        colorBack={colorBack} 
      />
      </div>

    </div>
  );
};

export default ListTask;
