import React, { useState, useEffect, useRef } from "react";
import WaterBar from "./Rive-Components/WaterBar.component.js";
import TodoList from './SubComponents/TodoComponent'
import useMouse from "@react-hook/mouse-position";


const ListTask = (a) => {

  const [colorBack, setColorBack] = useState('rgb(3,15,22)');
  const [colorFont, setColorFont] = useState('white');

  const ref = useRef(null)
  const mouse =  useMouse(ref, {
    enterDelay: 100,
    leaveDelay: 100,
  })

  const handleScroll = (e) =>{

    console.log('scroll');

  }



  return (
    <div 
    ref={ref}
    style={{
        width: "90vw",
        height: "100%",
        paddingLeft: '20px',
        marginTop:'20px',
        color: colorFont,
        overflow:'hidden',

      }}
    >
      <TodoList 
        style={{
          width: "100%",
          zIndex:2
          }}
        onScroll={(e)=>handleScroll(e)}
      />
      <div
        style={{
          width:'100%',
          height:'100%',
          position:'absolute',
          top:'40vw',
          left:'0',
          zIndex:-1,
          backgroundColor:'rgb(11,14,21)',
          overflow:'hidden',
        }}
      >

      <WaterBar 
        scroll={mouse.pageY/80} 
        colorBack={colorBack} 
      />

      </div>

    </div>
  );
};

export default ListTask;
