import React from "react";
import Rive,{ useRive,useStateMachineInput } from 'rive-react';

import waterRive  from './715-1406-water-bar-demo.riv'

const WaterBar = ({scroll,colorBack}) => {


    const STATE_MACHINE_NAME = 'State Machine'
    const INPUT_NAME_1 = 'Level'

    const { rive, RiveComponent } = useRive({
        src: waterRive,
        stateMachines:STATE_MACHINE_NAME,
        artboard: 'New Artboard',
        autoplay: true,

    });

    const ValueInput = useStateMachineInput(
        rive,
        STATE_MACHINE_NAME,
        INPUT_NAME_1
    );

    const Bar = (colorBack) => {
        return(
            <div
                style={{
                    backgroundColor:colorBack,
                    width: '20%',
                    height:'100%',
                    zIndex
                }}
            >

            </div>
        )

    }


    return (
        <div 
        style={{
            backgroundColor:colorBack,
            width:'10vw'

        }}>
            <Bar />

        <RiveComponent
            // onMouseEnter={() => ValueInput.value = true }
            onClick={
                () => ValueInput.value=(scroll===null?20:scroll)
            }
            // fill={fill}            
        />

            <Bar />


        </div>


    );

}

export default WaterBar
